import React, { createContext, useState } from "react";

export const ContextoOrden = createContext();

export const OrdenProvider = ({ children }) => {
	const [orden, setOrden] = useState([]);

	const addToOrder = (bebida) => {
		setOrden([...orden, bebida]);
	};

	const removeFromOrder = (bebidaId) => {
		setOrder(orden.filter((item) => item.id !== bebidaId));
	};

	const clearOrder = () => {
		setOrder([]);
	};

	return (
		<OrderContext.Provider value={{ orden, addToOrder, removeFromOrder, clearOrder }}>{children}</OrderContext.Provider>
	);
};
